Alias.script:
fishing.dir = matches[2]
echo("Direction is: "..fishing.dir)
send(" ")

fishing.bait = matches[2]
echo("Bait is: "..fishing.bait)
send(" ")

fishing.distance = matches[2]
echo("Distance is: "..fishing.distance)
send(" ")

targ = matches[2]
echo("Target: "..targ)
send(" ")

reset_all()
echo("RESET!")
send(" ")

cecho("<green>\nBasic Commands:")
echo("\n halp: this, obviously")
echo("\n sts: display toggles")
echo("\n t X: set combat target X")
echo("\n rst: reset most variables to default")
echo("\n ldd X: load defense option X")
echo("\n kd X: toggle individual defense X")
cecho("<green>\nBasic Toggles:")
echo("\n hl: toggle healing on/off")
echo("\n ss: toggle standing on/off")
echo("\n dfnd: toggle defending on/off")
echo("\n kil: toggle killing on/off")
echo("\n tbld: toggle curing bleeding on/off")
echo("\n tcl: toggle closing pack on/off")
echo("\n tpr: toggle prompt display to numbers/percents")
if my.class == "soldier" then
 echo("\n tmch: toggle marching on/off")
end
cecho("<green>\nUtility Commands:")
echo("\n mnt: mount/vault your mount")
echo("\n dm: dismount")
echo("\n folm: lead your mount")
echo("\n pnh: punch/stop your mount, to stay in combat")
echo("\n lm X: PATH FIND to landmark X")
echo("\n cfs: credits for sale")
cecho("<green>\nBashing Commands:")
echo("\n ff: toggle fasthunt on/off")
echo("\n aa: start bashing current room")
echo("\n ast: stop bashing")
echo("\n flee: toggle bashing/fasthunting off and queue flee")
echo("\n tgg: toggle gathering on/off")
echo("\n gen: toggle generic/numeric targeting")
echo("\n offr: check IH CORPSE and then offer all the corpses you hold")
if my.class == "soldier" then
 echo("\n brk: toggle berserking on/off")
 echo("\n trt: toggle threatening on/off")
 echo("\n tsnd: toggle sundering on/off")
elseif my.class == "rogue" then
 echo("\n tflh: toggle fleching on/off")
 echo("\n tsm: toggle shadowmending on/off")
elseif my.class == "priest" then
 echo("\n thrt: toggle heretic on/off")
 echo("\n tsv: toggle savaging on/off")
elseif my.class == "magician" then
 echo("\n tigt: toggle igniting on/off")
end
cecho("<green>\nFishing Commands:")
echo("\n fsh: toggle fishing on/off")
echo("\n dr X: set fishing direction to X")
echo("\n cbait X: set bait type to X")
echo("\n fdis X: set cast distance to X")
send(" ")
